'declare
    cr varchar2(100);
    l_url varchar2(2000);
    tref    varchar2(1000);

begin

    tref := OWA_UTIL.get_cgi_env(''HTTP_REFERER'');
    --htp.p(tref);
    begin
    cr := OAUTH_PKG.auth(:client_id, :usr, :pwd, tref);
    exception
    when others then
            :status_code := 403;
    end;
    --client doesn''t exist
    if cr = ''-3'' then
        htp.p(cr); 
    --login failed
    elsif cr = ''-1'' then
        htp.p(cr);
    -- ok, we redirect with the token value    
    else
        begin
            select redirect_uri
            into l_url
            from user_ords_clients
                where client_id= :client_id;
            exception
                when others then
                    cr := ''-4'';
                    --htp.p(cr);
                    :status_code := 403;
        end;
        /*
        begin
        
            for i in 1..nvl(owa.num_cgi_vars, 0) loop
                htp.p(owa.cgi_var_name(i) || '': '' || owa.c' || 'gi_var_val(i) ||''<br>'');
            end loop;
        end;
        */ 
                
        if length(cr) > 5 then
            :forward_location := l_url||''#token=''||cr;
            :status_code := 302;
        else
            :status_code := 403;
        end if;
            
    end if;
end;
');

